import { request, response } from 'express'
import { connect } from './../database/database';
import bcryptjs from 'bcryptjs'

//Registramos el usuario
const registerUser = async (req = request, res = response) => {

    //Obtenemos los datos desde el body
    const {
        username,
        first_name,
        last_name,
        email,
        pass,
        groups,
        user_permission,
        is_staff,
        is_active,
        is_superuser
    } = req.body

    //Encriptamos la contraseña
    let passwordHash = bcryptjs.hashSync(pass, 8)
    //Fecha actual
    const today = Date.now()
    const date = new Date(today)
    const formattedDate = date.toLocaleDateString()

    //Creamos el objeto
    const user = {
        username: username,
        first_name: first_name,
        last_name: last_name,
        email: email,
        pass: passwordHash,
        groups: groups,
        user_permission: user_permission,
        is_staff: is_staff,
        is_active: is_active,
        is_superuser: is_superuser,
        last_login: formattedDate,
        date_joined: formattedDate
    }

    try {
        //Instanciamos la conexion
        const connection = await connect

        //Insertamos el objeto
        const result = await connection.query('INSERT INTO users SET ?', user)

        res.status(200).json({
            ok: true,
            user,
            result,
            message: 'Todo ok'
        })
    } catch (err) {
        res.status(400).json({
            ok: false,
            err,
            message: 'Algo salio mal'
        })
    }
}

//Obtenemos los usuarios
const getUsers = async (req = request, res = response) => {
    try {
        const connection = await connect

        const result = await connection.query('SELECT * FROM users')

        res.status(200).json({
            ok: true,
            result,
            message: 'Todo ok'
        })
    } catch (err) {
        res.status(400).json({
            ok: false,
            err,
            message: 'Algo salio mal'
        })
    }
}

//Obtenemos un usuario especifico
const getUser = async (req = request, res = response) => {
    const user_id = req.params.id

    try {
        const connection = await connect

        const result = await connection.query('SELECT * FROM users WHERE user_id = ?', user_id)

        res.status(200).json({
